    data_criacao DATE,
    data_transferencia DATE,
    data_finalizacao DATE,
    protocolo BIGINT,
    telefone VARCHAR,
    tempo_de_atendimento_horas INT,
    tempo_de_atendimento_minutos INT,
    tempo_de_atendimento_segundos INT,
    tempo_de_atendimento_humano_horas INT,
    tempo_de_atendimento_humano_minutos INT,
    tempo_de_atendimento_humano_segundos INT,
    tempo_em_espera_horas INT,
    tempo_em_espera_minutos INT,
    tempo_em_espera_segundos INT,
    tempo_em_fila_horas INT,
    tempo_em_fila_minutos INT,
    tempo_em_fila_segundos INT,
    data_ultima_msg_agente DATE,
    data_ultima_msg_cliente DATE,
